document.addEventListener("DOMContentLoaded",function(){for(var e=document.querySelectorAll('.artru-noanchor a[href^="#"], a[class*="artru-noanchor"]'),t=0;t<e.length;t++)e[t].addEventListener("click",function(e){e.preventDefault()});var n=document.querySelector(".artru-scrollTop"),r=document.querySelector("#top");n.addEventListener("click",function(){window.scrollTo(0,r.offsetTop)});var o=document.getElementById("artru-language_switcher"),l=document.getElementById("hide-box-lang"),a=document.getElementById("current-lang");o.addEventListener("mouseover",function(){l.style.display="block"}),o.addEventListener("mouseout",function(){l.style.display="none"}),a.addEventListener("touchstart",function(){"none"===l.style.display?l.style.display="block":l.style.display="none"},{passive:!0})});